// https://leetcode.com/problems/implement-strstr

import Tests, { holog } from '../TestHelpers'

function strStr1(haystack: string, needle: string): number {
  for (let i = 0; i < haystack.length - needle.length + 1; i++) {
    const subStr = haystack.substring(i, i + needle.length)
    if (subStr === needle) {
      return i
    }
  }
  return -1
}

function charToNum(c: string): number {
  return c.charCodeAt(0) - 96
}

function hashNoOrder(s: string): number {
  // a + b + c
  // 1 + 2 + 3
  // => 6
  // ---------
  // c + b + a
  // 3 + 2 + 1
  // => 6
  return [...s].reduce((result, c) => result + charToNum(c), 0)
}

function hashWithOrder(s: string): number {
  //  a              +  b              +  c
  // (1 * (10 ** 2)) + (2 * (10 ** 1)) + (3 * (10 ** 0))
  //  100            +  20            +   3
  // => 123
  // ---------------------------------------------------
  //  c              +  b              +  a
  // (3 * (10 ** 2)) + (2 * (10 ** 1)) + (1 * (10 ** 0))
  //  300            +  20             +  1
  // => 321
  let result = 0
  for (let i = 0; i < s.length; i++) {
    let c = s[i]
    let m = 10 ** (s.length - 1 - i)
    result += charToNum(c) * m
  }
  return result
}

function hashWithModulo(s: string): number {
  // Integer overflow can cause hash collisions.
  // Using modulo, we can reduce the chances of a collision.
  return hashWithOrder(s) % Number.MAX_VALUE
}

function strStr2(haystack: string, needle: string): number {
  let hash = hashWithModulo(needle)
  let curr = hashWithModulo(haystack.substring(0, needle.length))
  if (curr === hash) {
    return 0
  }
  for (let i = 1; i < haystack.length - needle.length + 1; i++) {
    let prevCode = charToNum(haystack[i - 1])
    let nextCode = charToNum(haystack[i - 1 + needle.length])
    let prev = prevCode * 10 ** (needle.length - 1)
    let next = nextCode
    curr -= prev // subtract previous
    curr *= 10   // shift left by 1
    curr += next // add next value
    if (curr === hash) {
      return i
    }
  }
  return -1
}

let tests = new Tests(
  ['abcde', 'cde', 2],
  ['', '', 0],
  ['hello', 'll', 2],
  ['aaaaa', 'bba', -1],
  [
    "",
    "",
    -1
  ],
  [
    "abbbbbaabbaabaabbbaaaaabbabbbabbbbbaababaabbaabbbbbababaababbbbaaabbbbabaabaaaabbbbabbbaabbbaabbaaabaabaaaaaaaa",
    "abbbaababbbabbbabbbbbabaaaaaaabaabaabbbbaabab",
    -1
  ]
)

tests.run(strStr2)
